const fs = require('fs')
 
// For read address.json
fs.readFile('address.json', (err,data)=>{
 
   // 1 example
   console.log(
       JSON.parse(data)
   )
 
   // 2 example
   let address = JSON.parse(data);
   console.log(`
   Country: ${address.country} - code ${address.short};
   State: ${address.state};
   City: ${address.city};
   Street: ${address.street};
   Number: ${address.number};
   Zip: ${address.zip}.
   `)
 
})